let MostRecentScanLogs = PurviewDataSensitivityLogs
| where ActivityType == "Classification" 
| where SourceType contains "Azure"
| extend ResourceType = iff(SourceType=="AzureBlob", "Storage account", "Amazon S3")
| summarize arg_max( AssetLastScanTime, *) by AssetPath, ActivityType;

let Sources = MostRecentScanLogs
| summarize count() by SourceName, ResourceType;

let SourcesCount = Sources
| summarize ResourcesCount = count() by ResourceType;

let Assets = MostRecentScanLogs
| summarize AssetCount = count() by ResourceType;

let ClassifiedAssets = MostRecentScanLogs
| where Classification != "[]"
| summarize AssetClassifiedCount = count() by ResourceType;

let ClassifiedAssetsByResourceType = Assets
| join kind= leftouter ClassifiedAssets on ResourceType
| extend ClassifiedPercentage = round((100.0 * AssetClassifiedCount / AssetCount),2)
| join kind= leftouter SourcesCount on ResourceType
| project ResourceType, ResourcesCount, AssetCount, AssetClassifiedCount, ClassifiedPercentage;

ClassifiedAssetsByResourceType

